mapper.update:
update childword
set wordid = #{wordid,jdbcType=INTEGER},
  childid = #{childid,jdbcType=INTEGER},
  righttimes = #{righttimes,jdbcType=INTEGER},
  errortimes = #{errortimes,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select cw.id as id, cw.wordid as wordId, cw.childid as childId, cw.righttimes as rightTimes, cw.errortimes as errorTimes, wr. id as wordRoomId, wr.wordroomname as wordRoomName from childword as cw,wordinfo as w,childinfo as c ,wordroom as wr where cw.wordid = w.id and cw.childid = c.id and w.wordroomid = wr.id
and cw.childid = #{childId}
order by cw.errortimes asc

select

from childword
where  wordid = #{wordid} and childid = #{childId}
